var VarX = mdm.store.data__.variables;	
mdm.store.data__.variables.rotateX = mdm.store.Basic.number_1;

var scene = new THREE.Scene();
var camera = new THREE.PerspectiveCamera( 75, window.innerWidth/window.innerHeight, 0.1, 1000 );

var renderer = new THREE.WebGLRenderer();
renderer.setSize( window.innerWidth/3, window.innerHeight/3 );
document.getElementById("canvasHost").appendChild( renderer.domElement );

var geometry = new THREE.BoxGeometry( 1, 1.5, 1 );
//var material = new THREE.MeshBasicMaterial( { color: 0x0000ff } );
var material = new THREE.MeshNormalMaterial();
var cube = new THREE.Mesh( geometry, material );
scene.add( cube );

camera.position.z = 2;

var render = function () {
  requestAnimationFrame( render );

  cube.rotation.x += mdm.store.data__.variables.rotateX;
  cube.rotation.y += 0.01;
  cube.rotation.z += 0.01;

  renderer.render(scene, camera);
};

render();
